
var EJS_Static = function() {

  // private functions
  // 
  var validate_data = require('./validate_data').validate_data,
      prepare_data = require('./prepare_data').prepare_data,
      prepare_template = require('./template').prepare_template,
      prepare_layout = require('./layout').prepare_layout,
      create_file = require('./render').create_file;

  // 
  // end private functions

  // public functions
  // 
  return {

    // validate data before rendering
    validate_data: validate_data,

    // prepare data for rendering
    prepare_data: prepare_data,

    // template
    prepare_template: prepare_template,

    // layout
    prepare_layout: prepare_layout,

    // render
    create_file: create_file
  
  }
  // 
  // end public functions

};

module.exports = exports = new EJS_Static();