'use strict';

module.exports = function(grunt) {

  // create grunt multitask
  grunt.registerMultiTask('ejs_static', 'Render EJS templates as static HTML.', function() {

    // get ejs_static methods
    var ejs_static = require('../lib/ejs_static');

    // set defaults for options
    var options = this.options({
      dest: 'preview',
      parent_dirs: false,
      file_extension: '.html',
      underscores_to_dashes: true
    });

    // start the ejs_static task
    ejs_static.validate_data(options, ejs_static);

    // ejs_static.create_files(the_data, options, ejs_static);

  });

};
